import React from "react";
import PropTypes from "prop-types";

const TodoItem = (props) => {
  const getStyle = () => {
    return {
      background: "#f4f4f4",
      padding: "10px",
      borderBottom: "1px #ccc dotted",
      textDecoration: props.todo.completed ? "line-through" : "none",
    };
  };

  const { id, title } = props.todo;
  return (
    <div style={getStyle}>
      <p>
        <input type="checkbox" onChange={props.markComplete.bind(id)} /> {title}
        <button onClick={props.delTodo.bind(id)} style={buttonStyle}>
          x
        </button>
      </p>
    </div>
  );
};

const buttonStyle = {
  background: "#ff0000",
  color: "#fff",
  border: "none",
  padding: "5px 9px",
  borderRadius: "50%",
  cursor: "pointer",
  float: "right",
};

TodoItem.propTypes = {
  todos: PropTypes.array.isRequired,
  markComplete: PropTypes.func.isRequired,
  delTodo: PropTypes.func.isRequired,
};

export default TodoItem;
